import java.util.Arrays;
import java.util.Scanner;

class PersonSortable implements Comparable<PersonSortable> {
    private String name;
    private int age;

    public PersonSortable(String name, int age) {
        this.name = name;
        this.age = age;
    }

    @Override
    public String toString() {
        return name + "-" + age;
    }

    @Override
    public int compareTo(PersonSortable other) {
        // 先按name升序排序
        int nameComparison = this.name.compareTo(other.name);
        if (nameComparison != 0) {
            return nameComparison;
        }
        // name相同时，按age升序排序
        return Integer.compare(this.age, other.age);
    }
}

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        // 1. 首先输入n
        int n = scanner.nextInt();
        scanner.nextLine(); // 消耗换行符

        // 2. 输入n行name age，并创建n个对象放入数组
        PersonSortable[] people = new PersonSortable[n];
        for (int i = 0; i < n; i++) {
            String name = scanner.next();
            int age = scanner.nextInt();
            people[i] = new PersonSortable(name, age);
        }

        // 3. 对数组进行排序后输出
        Arrays.sort(people);
        for (PersonSortable person : people) {
            System.out.println(person);
        }

        // 4. 输出PersonSortable所实现的所有接口
        System.out.println(Arrays.toString(PersonSortable.class.getInterfaces()));

        scanner.close();
    }
}

import java.util.Arrays;
import java.util.Comparator;
import java.util.Scanner;

class PersonSortable2 {
    private String name;
    private int age;

    public PersonSortable2(String name, int age) {
        this.name = name;
        this.age = age;
    }

    public String getName() {
        return name;
    }

    public int getAge() {
        return age;
    }

    @Override
    public String toString() {
        return name + "-" + age;
    }
}

class NameComparator implements Comparator<PersonSortable2> {
    @Override
    public int compare(PersonSortable2 p1, PersonSortable2 p2) {
        return p1.getName().compareTo(p2.getName());
    }
}

class AgeComparator implements Comparator<PersonSortable2> {
    @Override
    public int compare(PersonSortable2 p1, PersonSortable2 p2) {
        return Integer.compare(p1.getAge(), p2.getAge());
    }
}

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        // 1. 输入n
        int n = scanner.nextInt();
        scanner.nextLine(); // 消耗换行符

        // 2. 输入n行name age，并创建n个对象放入数组
        PersonSortable2[] people = new PersonSortable2[n];
        for (int i = 0; i < n; i++) {
            String name = scanner.next();
            int age = scanner.nextInt();
            people[i] = new PersonSortable2(name, age);
        }

        // 3. 对数组按照name进行升序排序后输出
        System.out.println("NameComparator:sort");
        Arrays.sort(people, new NameComparator());
        for (PersonSortable2 person : people) {
            System.out.println(person);
        }

        // 4. 在3的基础上对数组按照age进行升序排序后输出
        System.out.println("AgeComparator:sort");
        Arrays.sort(people, new AgeComparator());
        for (PersonSortable2 person : people) {
            System.out.println(person);
        }

        // 5. 输出NameComparator与AgeComparator所实现的所有接口
        System.out.println(Arrays.toString(NameComparator.class.getInterfaces()));
        System.out.println(Arrays.toString(AgeComparator.class.getInterfaces()));

        scanner.close();
    }
}

import java.util.Scanner;

abstract class BallMatch {
    protected String result;

    // 模板方法，定义比赛流程
    public void compete() {
        checkin();
        start();
        play();
        end();
        announceResult();
    }

    // 共同步骤
    private void checkin() {
        System.out.println("now checking in");
    }

    private void start() {
        System.out.println("now starting");
    }

    private void end() {
        System.out.println("now ending");
    }

    // 抽象方法，由子类实现
    protected abstract void play();

    // 具体方法，由子类调用
    protected void announceResult() {
        System.out.println("now annoucing result: " + result);
    }
}

class FootballMatch extends BallMatch {
    public FootballMatch(String result) {
        this.result = result;
    }

    @Override
    protected void play() {
        System.out.println("now playing football");
    }
}

class BasketBallMatch extends BallMatch {
    public BasketBallMatch(String result) {
        this.result = result;
    }

    @Override
    protected void play() {
        System.out.println("now playing basketball");
    }
}

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        // 读取比赛类型和比分
        int type = scanner.nextInt();
        String result = scanner.next();

        // 根据类型创建不同的比赛
        BallMatch match;
        if (type == 1) {
            match = new FootballMatch(result);
        } else {
            match = new BasketBallMatch(result);
        }

        // 打印比赛过程
        match.compete();

        scanner.close();
    }
}

import java.util.Scanner;

// 车辆基类
abstract class Car {
    protected String name;
    protected int rent; // 每日租金

    public Car(String name, int rent) {
        this.name = name;
        this.rent = rent;
    }

    public abstract int getPassengerCapacity(); // 载客量
    public abstract double getCargoCapacity(); // 载货量

    public int calculateRent(int days) {
        return rent * days;
    }
}

// 客车类
class PassengerCar extends Car {
    private int passengerCapacity;

    public PassengerCar(String name, int rent, int passengerCapacity) {
        super(name, rent);
        this.passengerCapacity = passengerCapacity;
    }

    @Override
    public int getPassengerCapacity() {
        return passengerCapacity;
    }

    @Override
    public double getCargoCapacity() {
        return 0;
    }
}

// 货车类
class Truck extends Car {
    private double cargoCapacity;

    public Truck(String name, int rent, double cargoCapacity) {
        super(name, rent);
        this.cargoCapacity = cargoCapacity;
    }

    @Override
    public int getPassengerCapacity() {
        return 0;
    }

    @Override
    public double getCargoCapacity() {
        return cargoCapacity;
    }
}

// 皮卡车类
class Pickup extends Car {
    private int passengerCapacity;
    private double cargoCapacity;

    public Pickup(String name, int rent, int passengerCapacity, double cargoCapacity) {
        super(name, rent);
        this.passengerCapacity = passengerCapacity;
        this.cargoCapacity = cargoCapacity;
    }

    @Override
    public int getPassengerCapacity() {
        return passengerCapacity;
    }

    @Override
    public double getCargoCapacity() {
        return cargoCapacity;
    }
}

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        // 初始化所有车型
        Car[] cars = {
            new PassengerCar("奥迪A4", 500, 4),
            new PassengerCar("马自达6", 400, 4),
            new Pickup("皮卡雪6", 450, 4, 2),
            new PassengerCar("金龙", 800, 20),
            new Truck("松花江", 400, 4),
            new Truck("依维柯", 1000, 20)
        };

        // 读取是否租车
        int rentCar = scanner.nextInt();

        if (rentCar == 0) {
            System.out.println("0 0.00 0");
            scanner.close();
            return;
        }

        // 读取租车数量
        int n = scanner.nextInt();

        int totalPassengers = 0;
        double totalCargo = 0;
        int totalRent = 0;

        // 处理每辆租车
        for (int i = 0; i < n; i++) {
            int carId = scanner.nextInt();
            int days = scanner.nextInt();

            // 车辆ID从1开始，数组索引从0开始
            Car selectedCar = cars[carId - 1];

            totalPassengers += selectedCar.getPassengerCapacity() * days;
            totalCargo += selectedCar.getCargoCapacity() * days;
            totalRent += selectedCar.calculateRent(days);
        }

        // 输出结果
        System.out.printf("%d %.2f %d\n", totalPassengers, totalCargo, totalRent);

        scanner.close();
    }
}

import java.util.Scanner;

// 抽象快递类
abstract class Express {
    protected int weight; // 快递重量(kg)

    public Express(int weight) {
        this.weight = weight;
    }

    public int getWeight() {
        return weight;
    }

    public abstract int getTotal(); // 计算快递运费
}

// 顺路快递类
class SLExpress extends Express {
    public SLExpress(int weight) {
        super(weight);
    }

    @Override
    public int getTotal() {
        // 首重(1kg)12元，每增加1kg费用加2元
        if (weight <= 1) {
            return 12;
        } else {
            return 12 + (weight - 1) * 2;
        }
    }
}

// 地地快递类
class DDExpress extends Express {
    public DDExpress(int weight) {
        super(weight);
    }

    @Override
    public int getTotal() {
        // 首重(1kg)5元，每增加1kg费用加1元
        if (weight <= 1) {
            return 5;
        } else {
            return 5 + (weight - 1) * 1;
        }
    }
}

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        // 读取快递件数
        int n = scanner.nextInt();

        int totalCost = 0;

        // 处理每个快递
        for (int i = 0; i < n; i++) {
            String company = scanner.next();
            int weight = scanner.nextInt();

            Express express;
            if (company.equals("SL")) {
                express = new SLExpress(weight);
            } else {
                express = new DDExpress(weight);
            }

            totalCost += express.getTotal();
        }

        // 输出总运费
        System.out.println(totalCost);

        scanner.close();
    }
}
